--
-- Cấu trúc bảng cho bảng `cuahang`
--

CREATE TABLE `cuahang` (
  `id` int(10) UNSIGNED NOT NULL,
  `ten` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `ten_khong_dau` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `gioi_thieu` text COLLATE utf8_unicode_ci NOT NULL,
  `link` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `vi_chi` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `cuahang`
--

INSERT INTO `cuahang` (`id`, `ten`, `ten_khong_dau`, `gioi_thieu`, `link`, `vi_chi`, `created_at`, `updated_at`) VALUES
(1, 'Bách Khoa Restaurent', 'bach-khoa-restaurent', '<p>Phạm Văn Khánh (sinh năm 1992). Khánh từng là Thủ khoa đầu vào đại học Bách khoa Hà Nội năm 2010. Mới đây, Khánh được bầu chọn là điển hình xuất sắc của Viettel năm 2016. Sở thích của Phạm Khánh là đọc sách (kĩ thuật, tiểu thuyết), chơi cầu lông, chơi game và tìm hiểu bảo mật. Hiện tại Khánh dành nhiều thời gian cho công việc. Khi rảnh rỗi, anh thường mày mò, tìm hiểu thêm về bảo mật thông tin.</p>', 'https://www.facebook.com/bkfoodcourt/', 'Bach Khoa Restaurant', '2017-05-05 06:48:21', '2017-05-09 14:25:38'),
(2, 'Ốc Nướng Trần Đại Ngĩa', 'oc-nuong-tran-dai-ngia', '<p>Đa dạng, ngon, rẻ, Trần Đại Nghĩa được ví như khu phố ẩm thực của các bạn trẻ quanh khu vực Đại học Kinh tế Quốc dân, Đại học Xây dựng và Đại học Bách Khoa Hà Nội. Không chỉ nổi tiếng với bánh mì chảo Hiệu Lực hay Sữa chua Ông già Tóc bạc, khu vực này còn rất nhiều món ăn hấp dẫn đang chờ bạn khám phá. Cùng Vi vu khám phá nhé!</p>', 'https://www.foody.vn/ha-noi/oc-mut-chan-ga-nuong-tran-dai-nghia', 'Quán+Ốc+%26+Lẩu+Nướng+Dũng+Béo', '2017-05-05 06:50:54', '2017-05-09 10:10:08'),
(4, 'Bạch Mai Lẩu-Nướng-Chiên Sinh Viên', 'bach-mai-lau-nuong-chien-sinh-vien', '<p>Lozi là một ứng dụng đăng bán được yêu thích nhất Việt Nam. Ngoài các chia sẻ món ăn mang đậm trải nghiệm cá nhân, bạn còn có thể đăng bán miễn phí trên Lozi tất tật mọi thứ: mỹ phẩm tự làm, quần áo cũ mới đủ loại, sách truyện không đọc nữa, cả mấy món do chính tay bạn nấu...&nbsp;</p>', 'https://lozi.vn/b/pepperonis-restaurant-bach-mai-492-bach-mai-quan-hai-ba-trung-ha-noi-283', 'Quan+An+Ngon+18+Phan+Boi+Chau', '2017-05-06 16:07:03', '2017-05-09 10:12:38'),
(5, 'Bách Khoa Ốc Nướng', 'bach-khoa-oc-nuong', '<p><strong>Độc đáo, ngon và rẻ là những từ mà giới trẻ xung quanh trường Đại học Bách Khoa Hà Nội đánh giá về ẩm thực khu phố Tạ Quang Bửu. Là địa điểm tập trung của đông đảo học sinh, sinh viên nên khu vực này có đủ các món ăn ngon từ dân dã, vỉa hè đến hàng quán khang trang. Cùng Vi vu khám phá món ngon khu vực này nhé!</strong></p>', 'https://www.hust.edu.vn', 'Nhà+Hàng+Ngon', '2017-05-08 02:39:44', '2017-05-09 10:26:43'),
(6, 'Bách Khoa Mì Bánh-Pate-Susi', 'bach-khoa-mi-banh-pate-susi', '<p>Ngon vãi nồi</p>', 'https://www.bachkhoa.com.vn', 'Cửa+hàng+Sushi', '2017-05-08 10:18:37', '2017-05-09 10:28:48'),
(7, 'Bách Khoa Lẩu Thập Cẩm', 'bach-khoa-lau-thap-cam', '<p>Ngon,bổ,rẻ...</p>', 'googlemap', 'Lẩu+Thập+Cẩm+Hằng+Linh+144,+Ngọc+Lâm,+Phường+Ngọc+Lâm,+Quận+Long+Biên,+Thành+Phố+Hà+Nội', '2017-05-08 10:21:14', '2017-05-09 10:29:53'),
(8, 'Bách Khoa Gà Nướng', 'bach-khoa-ga-nuong', '<p>Ngon vãi nồi ra ý,bạn nào muấn thử thì súc thôi</p>\r\n\r\n<p>&nbsp;</p>', 'google.com', 'Bún+Chả+Sinh+Từ+57+Nguyễn+Khuyến', '2017-05-08 10:47:16', '2017-05-09 11:13:46'),
(9, 'Xây Dựng Trà Đá', 'xay-dung-tra-da', '<p>Ngon,rẻ,nhanh nhưng không bổ</p>', 'http://www.nuce.trada.com.vn', 'Cửa+Hàng+Mía+Đá+-+Trà+Đá', '2017-05-09 02:46:56', '2017-05-09 11:20:28'),
(10, 'Bách Khoa Xôi Xéo', 'bach-khoa-xoi-xeo', '<p><span style=\"color:rgb(34, 34, 34); font-family:noto serif,serif; font-size:18px\">Nhớ thủa nhỏ sáng đi học vội vàng, tôi lại chạy ra đầu ngõ mua một nắm xôi xéo nhỏ bỏ vào cặp sách chạy vội đến trường. Xôi có màu vàng ruộm được bọc lá sen thơm, phủ trên từng lát xéo đậu cũng vàng óng ả, béo bùi bởi lớp mỡ thơm và thật nhiều hành phi giòn, ngon miệng, ngon mắt đến lạ lùng. Trong tiết trời lạnh buốt của mùa đông, gói xôi nóng hổi, ăn miếng nào ấm cái bụng đến đó.</span></p>', 'http://nhahangmonhue.vn/', 'Xôi+Xéo', '2017-05-09 09:55:30', '2017-05-09 11:25:12');

